hudson.tasks.Shell.command:
#!/bin/bash CLUSTER_NAME=visage-api-cluster-prod
set -e
#Set the credentials to be able to pull from private directory and deploy the services EXTRA_ECS_CONFIG="ECS_CLUSTER=${CLUSTER_NAME}\nECS_ENGINE_AUTH_TYPE=dockercfg\nECS_ENGINE_AUTH_DATA={\"https://index.docker.io/v1/\":{\"auth\":\"YWRtaW52aXNhZ2U6MjMmJl9PanVs\",\"email\":\"adminvisage\"}}" REGION=us-west-2 KEY_FILE=/var/lib/jenkins/cluster-a-key.pem echo $EXTRA_ECS_CONFIG
print_instance_ips() { INSTANCE_ARNS=$(aws --region $REGION ecs list-container-instances --cluster $CLUSTER_NAME --query 'containerInstanceArns[*]' --output text) INSTANCE_IDS=$(aws --region $REGION ecs describe-container-instances --cluster $CLUSTER_NAME --container-instances $INSTANCE_ARNS --query 'containerInstances[*].ec2InstanceId' --output text) aws --region $REGION ec2 describe-instances --instance-ids $INSTANCE_IDS --query 'Reservations[*].Instances[*].PublicIpAddress' --output text }
/usr/local/bin/ecs-cli configure --region $REGION --cluster $CLUSTER_NAME echo -n "Waiting for instances to be up to add auth credentials (this may take a while) ..." while ! INSTANCE_IPS=$(print_instance_ips 2> /dev/null) || [ "$(echo $INSTANCE_IPS | wc -w)" -ne $CLUSTER_SIZE ] ; do sleep 5 done echo "done" SSH_ARGS="-i $KEY_FILE -o StrictHostKeyChecking=no ec2-user@" echo $KEY_FILE echo $SSH_ARGS for IP in $INSTANCE_IPS; do echo $IP #delete old default and auth cluster configuration don't fail if nothing to remove ssh $SSH_ARGS$IP -t -t sudo bash -c "'sudo rm /var/lib/ecs/data/ecs_agent_data.json || true'" ssh $SSH_ARGS$IP -t -t sudo bash -c "'echo -e $(printf %q $EXTRA_ECS_CONFIG) > /etc/ecs/ecs.config'" #don't fail if nothing to stop ssh $SSH_ARGS$IP -t -t sudo bash -c "'sudo stop ecs || true'" ssh $SSH_ARGS$IP -t -t sudo start ecs #wait for ecs agent to start properly n=0 until [ $n -ge 5 ] do ssh $SSH_ARGS$IP -t -t curl http://localhost:51678/v1/metadata && break n=$[$n+1] sleep 2 done done
